import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // We will use axios (or API) for the POST request

const AdminDashboard = () => {
    const [activityLogs, setActivityLogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('adminToken');
        if (!token) {
            navigate('/admin/auth');
            return;
        }
        fetchActivityLogs(token); // Pass token to fetch logs
    }, [navigate]);

    const fetchActivityLogs = async (token) => {
        try {
            // Note: Updated to use axios/fetch with token for authentication
            const response = await axios.get('http://localhost:8080/api/admin/activity-logs', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            setActivityLogs(response.data);
        } catch (err) {
            console.error('Error fetching activity logs:', err);
            // Handle unauthorized or token expiry
            if (err.response && err.response.status === 401) {
                localStorage.removeItem('adminToken');
                navigate('/admin/auth');
            }
            setError('Failed to fetch activity logs');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        const token = localStorage.getItem('adminToken');
        if (token) {
            try {
                // Call the backend endpoint to log the logout time
                await axios.post('http://localhost:8080/api/admin/auth/logout', null, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                    },
                });
                console.log('Admin logout activity logged.');
            } catch (error) {
                // Log the error but proceed with client-side logout anyway
                console.error('Error logging out on backend:', error);
            }
        }
        
        // Client-side cleanup
        localStorage.removeItem('adminToken');
        localStorage.removeItem('adminUsername');
        navigate('/'); // Redirect to the main page or admin login
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleString();
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="text-lg">Loading...</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50">
            {/* Header */}
            <header className="bg-white shadow">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
                    <div>
                        <h1 className="text-2xl font-bold text-gray-900">Admin Dashboard</h1>
                        <p className="text-sm text-gray-600">
                            Welcome, {localStorage.getItem('adminUsername')}
                        </p>
                    </div>
                    <button
                        onClick={handleLogout}
                        className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
                    >
                        Logout
                    </button>
                </div>
            </header>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                {error && (
                    <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
                        {error}
                    </div>
                )}

                {/* Activity Logs Section */}
                <div className="bg-white shadow rounded-lg">
                    <div className="px-6 py-4 border-b border-gray-200">
                        <h2 className="text-lg font-medium text-gray-900">Admin Activity Logs</h2>
                        <p className="mt-1 text-sm text-gray-600">
                            Recent activities performed by admins
                        </p>
                    </div>
                    <div className="overflow-x-auto">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Admin
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Action
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Description
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        IP Address
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Timestamp
                                    </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {activityLogs.map((log) => (
                                    <tr key={log.id}>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                            {/* FIX: Added optional chaining (log.admin?.username) to prevent crash */}
                                            {log.admin?.username || 'Admin Removed'} 
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                            {log.action}
                                        </td>
                                        <td className="px-6 py-4 text-sm text-gray-500">
                                            {log.description}
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                            {log.ipAddress}
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                            {formatDate(log.timestamp)}
                                        </td>
                                    </tr>
                                ))}
                                {activityLogs.length === 0 && (
                                    <tr>
                                        <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500">
                                            No activity logs found
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default AdminDashboard;
