import React, { createContext, useContext, useEffect, useState } from 'react';
import API from "../services/api";

const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [lockInfo, setLockInfo] = useState({});

  useEffect(() => {
    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    const savedUser = localStorage.getItem('healthyaura_user');

    if (token && username) {
      // Prioritize username from localStorage
      const userData = { 
        username: username, 
        token: token,
        role: (savedUser ? JSON.parse(savedUser).role : 'USER')
      };
      setUser(userData);
      API.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    } else if (savedUser) {
      // Fallback to saved user data
      const parsed = JSON.parse(savedUser);
      setUser(parsed);
      if(parsed.token){
        API.defaults.headers.common["Authorization"] = `Bearer ${parsed.token}`;
      }
    }
  }, []);

  const signIn = async (username, password) => {
    // client-side check for lockout
    const info = lockInfo[username];
    if (info && info.lockedUntil && new Date(info.lockedUntil) > new Date()) {
      throw new Error('Account locked. Try later.');
    }

    try {
        const res = await API.post("/auth/login", { "username": username, "password": password });
        console.log("Login response", res.data);
        const { token, username: uname, role } = res.data;

        if(!token) throw new Error("Invalid response from server");

        const userData = { username: uname, role, token };

        // Store in multiple places for redundancy
        localStorage.setItem("token", token);
        localStorage.setItem("username", uname); // This is crucial for navbar
        localStorage.setItem("healthyaura_user", JSON.stringify(userData));

        API.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        setUser(userData);
        setLockInfo((s) => ({ ...s, [username]: { attempts: 0 } }));
        return userData;
    } catch(err) {
        console.error("Login error:", err);
        setLockInfo((s) => {
            const curr = s[username] || { attempts: 0 };
            const attempts = curr.attempts + 1;
            const lockedUntil =
                attempts >= 3
                ? new Date(Date.now() + 30 * 60 * 1000).toISOString()
                : curr.lockedUntil;
            return { ...s, [username]: { attempts, lockedUntil } };
        });

        if (err.response && err.response.data) {
            throw new Error(err.response.data.message || err.response.data || "Login failed");
        } else if (err.message) {
            throw new Error(err.message);
        } else {
            throw new Error("The username or password is incorrect. Please try again.");
        }
    }
  };

  const signOut = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    localStorage.removeItem("healthyaura_user");
    localStorage.removeItem("adminToken");
    localStorage.removeItem("adminUsername");
    delete API.defaults.headers.common["Authorization"];
    setUser(null);
  };

  const signUp = async (payload) => {
    const res = await API.post("/auth/signup", payload);
    console.log("Signup response:", res.data);

    const { token, username, role } = res.data;
    const userData = { username, role, token };

    localStorage.setItem("token", token);
    localStorage.setItem("username", username);
    localStorage.setItem("healthyaura_user", JSON.stringify(userData));

    API.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    setUser(userData);

    return res.data;
  };

  return (
    <AuthContext.Provider value={{ user, signIn, signOut, signUp }}>
      {children}
    </AuthContext.Provider>
  );
}
