import React, { useEffect, useState } from "react";
import Layout from "../components/Layout";
import { useAuth } from "../contexts/AuthContext";
import API from "../services/api";

export default function Profile() {
  const { user, signOut } = useAuth();
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({ email: "", preferences: "", password: "" });
  const [editingPrefs, setEditingPrefs] = useState(false);
  const [editingEmail, setEditingEmail] = useState(false);
  const [editingPwd, setEditingPwd] = useState(false);
  const [showPwd, setShowPwd] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        // Try to fetch profile data, but handle case where endpoint might not exist
        const res = await API.get("/profile/me");
        setProfile(res.data);
        setForm({
          email: res.data.email || "",
          preferences: res.data.preferences || "",
          password: "",
        });
      } catch (err) {
        console.error("Error fetching profile:", err);
        // If profile endpoint doesn't exist, create basic profile from user context
        if (user) {
          setProfile({
            username: user.username,
            email: user.email || "Not set",
            preferences: "No preferences set",
            totalPoints: 0
          });
          setForm({
            email: user.email || "",
            preferences: "No preferences set",
            password: "",
          });
        }
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchProfile();
    } else {
      setLoading(false);
    }
  }, [user]);

  // ====== Handlers ======

  const handleSavePrefs = async () => {
    try {
      const res = await API.put("/profile/me", { preferences: form.preferences });
      setProfile(res.data);
      setEditingPrefs(false);
      alert("Preferences updated successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to update preferences. This feature might not be implemented yet.");
    }
  };

  const handleSaveEmail = async () => {
    try {
      const res = await API.put("/profile/me/email", { email: form.email });
      setProfile((p) => ({ ...p, email: res.data.email }));
      setEditingEmail(false);
      alert("Email updated successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to update email. This feature might not be implemented yet.");
    }
  };

  const handleChangePassword = async () => {
    try {
      await API.put("/profile/me/password", { password: form.password });
      setEditingPwd(false);
      setForm((f) => ({ ...f, password: "" }));
      alert("Password changed successfully!");
    } catch (err) {
      console.error(err);
      alert("Failed to change password. This feature might not be implemented yet.");
    }
  };

  if (loading) {
    return (
      <Layout>
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <p className="text-center text-gray-500">Loading profile...</p>
        </div>
      </Layout>
    );
  }

  if (!user) {
    return (
      <Layout>
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <p className="text-center text-gray-500">Please sign in to view your profile.</p>
        </div>
      </Layout>
    );
  }

  // Use user data from context if profile fetch failed
  const displayProfile = profile || {
    username: user.username,
    email: "Not set",
    preferences: "No preferences set",
    totalPoints: 0
  };

  return (
    <Layout>
      {/* Header */}
      <div className="bg-white rounded-2xl shadow overflow-hidden mb-6">
        <div className="h-24 bg-gradient-to-r from-green-600 to-emerald-500" />
        <div className="flex flex-col items-center -mt-10 pb-5 px-5">
          <div className="bg-white p-3 rounded-full shadow">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-16 w-16 text-green-600"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={1.5}
                d="M15.75 7.5a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0zM4.5 19.5a7.5 7.5 0 0115 0"
              />
            </svg>
          </div>

          <h2 className="mt-3 text-xl font-bold text-gray-900">
            {displayProfile.username}
          </h2>
          <p className="text-gray-600 text-sm">{displayProfile.email}</p>

          <div className="flex gap-3 mt-3">
            <div className="px-3 py-1 bg-green-100 text-green-700 rounded-full text-sm">
              {displayProfile.totalPoints ?? 0} Points
            </div>
            <div className="px-3 py-1 bg-emerald-50 text-emerald-700 rounded-full text-sm">
              ✅ Profile Active
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-6">
          {error}
        </div>
      )}

      {/* ===== Edit Email ===== */}
      <div className="bg-white rounded-2xl shadow p-5 mb-6">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-lg font-semibold text-gray-900">Email</h3>
          {!editingEmail ? (
            <button
              onClick={() => setEditingEmail(true)}
              className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
            >
              Edit
            </button>
          ) : (
            <div className="flex gap-2">
              <button
                onClick={handleSaveEmail}
                className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
              >
                Save
              </button>
              <button
                onClick={() => {
                  setEditingEmail(false);
                  setForm({ ...form, email: displayProfile.email });
                }}
                className="px-4 py-2 rounded-lg text-sm border"
              >
                Cancel
              </button>
            </div>
          )}
        </div>

        <input
          disabled={!editingEmail}
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          placeholder="Enter your email"
          className={`w-full p-2 rounded-lg border ${
            editingEmail ? "bg-white border-green-500" : "bg-gray-50"
          }`}
        />
      </div>

      {/* ===== Preferences ===== */}
      <div className="bg-white rounded-2xl shadow p-5 mb-6">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-lg font-semibold text-gray-900">Preferences</h3>
          {!editingPrefs ? (
            <button
              onClick={() => setEditingPrefs(true)}
              className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
            >
              Edit
            </button>
          ) : (
            <div className="flex gap-2">
              <button
                onClick={handleSavePrefs}
                className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
              >
                Save
              </button>
              <button
                onClick={() => {
                  setEditingPrefs(false);
                  setForm({ ...form, preferences: displayProfile.preferences });
                }}
                className="px-4 py-2 rounded-lg text-sm border"
              >
                Cancel
              </button>
            </div>
          )}
        </div>

        <input
          disabled={!editingPrefs}
          value={form.preferences}
          onChange={(e) => setForm({ ...form, preferences: e.target.value })}
          placeholder="e.g., Halal, Vegetarian, Low sugar"
          className={`w-full p-2 rounded-lg border ${
            editingPrefs ? "bg-white border-green-500" : "bg-gray-50"
          }`}
        />
      </div>

      {/* ===== Password ===== */}
      <div className="bg-white rounded-2xl shadow p-5 mb-6">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-lg font-semibold text-gray-900">Change Password</h3>
          {!editingPwd ? (
            <button
              onClick={() => setEditingPwd(true)}
              className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
            >
              Edit
            </button>
          ) : (
            <div className="flex gap-2">
              <button
                onClick={handleChangePassword}
                className="bg-green-600 text-white px-4 py-2 rounded-lg text-sm hover:bg-green-700"
              >
                Save
              </button>
              <button
                onClick={() => {
                  setEditingPwd(false);
                  setForm((f) => ({ ...f, password: "" }));
                }}
                className="px-4 py-2 rounded-lg text-sm border"
              >
                Cancel
              </button>
            </div>
          )}
        </div>

        <div className="flex gap-2 items-center">
          <input
            type={showPwd ? "text" : "password"}
            disabled={!editingPwd}
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            placeholder={editingPwd ? "Enter new password" : "••••••••"}
            className={`w-full p-2 rounded-lg border ${
              editingPwd ? "bg-white border-green-500" : "bg-gray-50"
            }`}
          />
          {editingPwd && (
            <button
              type="button"
              onClick={() => setShowPwd((s) => !s)}
              className="px-3 py-2 border rounded-lg text-sm hover:bg-gray-50"
            >
              {showPwd ? "Hide" : "Show"}
            </button>
          )}
        </div>
      </div>

      {/* Sign Out */}
      <div className="flex justify-center">
        <button
          onClick={signOut}
          className="px-6 py-3 bg-red-600 text-white rounded-lg text-sm hover:bg-red-700 transition-colors"
        >
          Sign Out
        </button>
      </div>

      {/* Info Message */}
      <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <p className="text-sm text-blue-700 text-center">
          Note: Some profile features might not be fully implemented in the backend yet.
          Your username is displayed from your login session.
        </p>
      </div>
    </Layout>
  );
}
