import React, { useEffect, useState } from "react";
import API from "../services/api";

export default function Explore() {
  const [q, setQ] = useState("");
  const [stalls, setStalls] = useState([]);
  const [tags, setTags] = useState([]);
  const [sortBy, setSortBy] = useState("distance");
  const [selectedStall, setSelectedStall] = useState(null);

  useEffect(() => {
    search(); // load initial results
  }, []);

  const toggleTag = (t) => {
    setTags((prev) =>
      prev.includes(t) ? prev.filter((x) => x !== t) : [...prev, t]
    );
  };

  const search = async () => {
    try {
      let res;
      if (q) {
        // search by query from backend
        res = await API.get("/api/eateries/fetchDb", { params: { query: q } });
      } else {
        // get all eateries
        res = await API.get("/api/eateries/fetchDb");
      }
      if (res.data) {
        console.log("Fetched eateries:", res.data);
        setStalls(res.data);
      }
    } catch (err) {
      console.error("Error fetching eateries:", err);
    }
  };

  const openMap = (stall) => {
    setSelectedStall(stall);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-semibold mb-4 text-green-800">Explore Eateries</h1>

      {/* Search bar */}
      <div className="flex gap-3 mb-3">
        <input
          value={q}
          onChange={(e) => setQ(e.target.value)}
          placeholder="Search by name or location"
          className="flex-1 p-2 border rounded"
        />
        <button
          onClick={search}
          className="bg-green-600 text-white px-4 rounded hover:bg-green-700"
        >
          Search
        </button>
      </div>

      {/* Tag filters and sorting */}
      <div className="flex flex-wrap items-center gap-3 mb-4">
        <div className="space-x-2">
          {["Vegan", "Vegetarian", "Halal", "Healthy", "High Protein"].map((t) => (
            <button
              key={t}
              onClick={() => toggleTag(t)}
              className={`px-2 py-1 border rounded ${
                tags.includes(t) ? "bg-green-100 text-green-700" : "bg-white"
              }`}
            >
              {t}
            </button>
          ))}
        </div>
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="distance">Distance</option>
          <option value="price">Average price</option>
          <option value="popularity">Popularity</option>
        </select>
      </div>

      {/* Results */}
      <div className="grid gap-3">
        {stalls.map((s) => (
          <div
            key={s.id}
            className="bg-white p-4 rounded shadow flex justify-between items-center hover:shadow-md transition"
          >
            <div>
              <div className="font-semibold text-green-800">{s.name}</div>
              <div className="text-xs text-gray-500">
                {s.address || s.fullAddress}
              </div>
              <div className="mt-2 text-xs text-gray-600">
                {s.tags?.join(" • ")}
              </div>
            </div>
            <div className="text-right">
              <button
                onClick={() => openMap(s)}
                className="mt-2 bg-green-600 text-white text-xs px-3 py-1 rounded hover:bg-green-700"
              >
                Get Directions
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* OneMap Iframe - shows when user clicks “Get Directions” */}
      {selectedStall && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold mb-2 text-green-800">
            Directions to {selectedStall.name}
          </h2>
          <iframe
            title="OneMap Directions"
            src={`https://www.onemap.gov.sg/main/v2/?lat=${selectedStall.latitude}&lng=${selectedStall.longitude}`}
            width="100%"
            height="500"
            className="border rounded"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </div>
  );
}
