import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Users, Hourglass, Star } from "lucide-react";
import BottomNav from "../components/BottomNav";
import API from "../services/api";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

/**
 * Eatery Details Page
 *
 * <p>The `DetailsPage` component displays the full details of a specific eatery,
 * including its location on a map, current crowd and queue information, and
 * user-generated reviews with health and hygiene ratings.</p>
 *
 * <p>It integrates multiple backend endpoints:
 * <ul>
 *   <li>`GET /api/eateries/{id}` – Fetch eatery details (name, address, coordinates)</li>
 *   <li>`GET /crowd/{id}` – Fetch current crowd and queue status</li>
 *   <li>`GET /api/eateries/{id}/reviews` – Retrieve user reviews</li>
 *   <li>`GET /api/eateries/{id}/reviews/ratings` – Get average health/hygiene scores</li>
 *   <li>`POST /api/eateries/{id}/reviews` – Submit new reviews</li>
 *   <li>`PUT /api/eateries/{id}/reviews/{reviewId}` – Update existing reviews</li>
 *   <li>`DELETE /api/eateries/{id}/reviews/{reviewId}` – Delete user reviews</li>
 *   <li>`POST /api/eateries/{id}/reviews/{reviewId}/flag` – Flag inappropriate reviews</li>
 * </ul>
 * </p>
 *
 * <p>Additional integrations:
 * <ul>
 *   <li>Map rendering using <b>Leaflet.js</b> with OneMap Singapore tiles</li>
 *   <li>Google Maps deep linking for directions</li>
 *   <li>Dynamic rating system with fractional stars</li>
 *   <li>Inline review editing and deletion for authenticated users</li>
 * </ul>
 * </p>
 *
 * @component
 * @example
 * // Example route
 * <Route path="/eatery/:id" element={<DetailsPage />} />
 *
 * @returns {JSX.Element} The eatery detail view with crowd info, map, and user reviews.
 *
 * @since 2025-11-07
 * @version 1.0
 */
export default function DetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  // === Eatery State ===
  const [stall, setStall] = useState(null);
  const [crowd, setCrowd] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [avgRatings, setAvgRatings] = useState(null);

  // === Review Form State ===
  const [healthScore, setHealthScore] = useState(0);
  const [hygieneScore, setHygieneScore] = useState(0);
  const [reviewText, setReviewText] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const [editingReviewId, setEditingReviewId] = useState(null);

  /**
   * Fetches the eatery details (name, address, coordinates).
   *
   * @async
   * @returns {Promise<void>}
   */
  useEffect(() => {
    const fetchStall = async () => {
      try {
        const res = await API.get(`/api/eateries/${id}`);
        setStall(res.data);
      } catch (err) {
        console.error("Failed to load eatery:", err);
      }
    };
    fetchStall();
  }, [id]);

  /**
   * Fetches real-time crowd and queue status for the eatery.
   *
   * @async
   * @returns {Promise<void>}
   */
  useEffect(() => {
    const fetchCrowd = async () => {
      try {
        const res = await API.get(`/crowd/${id}`);
        setCrowd(res.data);
      } catch (err) {
        console.error("Failed to load crowd info:", err);
      }
    };
    fetchCrowd();
  }, [id]);

  /**
   * Fetches all reviews for this eatery.
   *
   * @async
   * @returns {Promise<void>}
   */
  const fetchReviews = async () => {
    try {
      const res = await API.get(`/api/eateries/${id}/reviews`);
      setReviews(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("Failed to load reviews:", err);
    }
  };

  /**
   * Fetches average health and hygiene ratings.
   *
   * @async
   * @returns {Promise<void>}
   */
  const fetchAverageRatings = async () => {
    try {
      const res = await API.get(`/api/eateries/${id}/reviews/ratings`);
      setAvgRatings(res.data);
    } catch (err) {
      console.error("Failed to load average ratings:", err);
    }
  };

  useEffect(() => {
    fetchReviews();
    fetchAverageRatings();
  }, [id]);

  /**
   * Initializes and renders the Leaflet map centered on the eatery’s coordinates.
   *
   * @effect
   * @returns {void}
   */
  useEffect(() => {
    if (!stall || !stall.latitude || !stall.longitude) return;
    const existing = L.DomUtil.get("details-map");
    if (existing && existing._leaflet_id) existing._leaflet_id = null;

    const map = L.map("details-map", {
      center: [stall.latitude, stall.longitude],
      zoom: 17,
    });

    L.tileLayer(
      "https://www.onemap.gov.sg/maps/tiles/Original/{z}/{x}/{y}.png",
      { attribution: "Map data © OneMap Singapore" }
    ).addTo(map);

    L.marker([stall.latitude, stall.longitude])
      .addTo(map)
      .bindPopup(`<b>${stall.name}</b><br>${stall.address || ""}`)
      .openPopup();

    return () => map.remove();
  }, [stall]);

  /**
   * Handles form submission for creating or updating a review.
   *
   * @async
   * @param {React.FormEvent} e - Form submission event
   * @returns {Promise<void>}
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!healthScore || !hygieneScore || !reviewText) {
      alert("Please fill in all fields.");
      return;
    }

    setSubmitting(true);
    try {
      const payload = { healthScore, hygieneScore, textFeedback: reviewText };

      if (editingReviewId) {
        await API.put(`/api/eateries/${id}/reviews/${editingReviewId}`, payload);
        alert("Review updated!");
      } else {
        await API.post(`/api/eateries/${id}/reviews`, payload);
        alert("Review submitted!");
      }

      setHealthScore(0);
      setHygieneScore(0);
      setReviewText("");
      setEditingReviewId(null);
      fetchReviews();
      fetchAverageRatings();
    } catch (err) {
      console.error("Failed to submit review:", err);
      alert("You must wait 7 days before submitting a new review for this eatery");
    } finally {
      setSubmitting(false);
    }
  };

  /**
   * Deletes a specific review.
   *
   * @async
   * @param {number} reviewId - The review’s unique ID
   * @returns {Promise<void>}
   */
  const handleDelete = async (reviewId) => {
    if (!window.confirm("Are you sure you want to delete this review?")) return;
    try {
      await API.delete(`/api/eateries/${id}/reviews/${reviewId}`);
      alert("Review deleted.");
      fetchReviews();
      fetchAverageRatings();
    } catch (err) {
      console.error("Failed to delete review:", err);
    }
  };

  /**
   * Flags a review for moderation by an admin.
   *
   * @async
   * @param {number} reviewId - Review ID to flag
   * @returns {Promise<void>}
   */
  const handleFlag = async (reviewId) => {
    try {
      const reason = prompt("Enter reason for flagging this review:");
      if (!reason) return;
      await API.post(`/api/eateries/${id}/reviews/${reviewId}/flag`, { reason });
      alert("Review reported for moderation.");
    } catch (err) {
      console.error("Failed to flag review:", err);
    }
  };

  /**
   * Enables inline editing of an existing review.
   *
   * @param {Object} r - Review object
   */
  const startEditInline = (r) => {
    setEditingReviewId(r.id);
    setHealthScore(r.healthScore);
    setHygieneScore(r.hygieneScore);
    setReviewText(r.textFeedback);
  };

  /**
   * Cancels inline edit mode and resets form state.
   */
  const cancelEdit = () => {
    setEditingReviewId(null);
    setHealthScore(0);
    setHygieneScore(0);
    setReviewText("");
  };

  /**
   * Renders star icons dynamically for ratings display.
   *
   * @param {number} count - Total number of stars
   * @param {number} activeCount - Number of filled stars
   * @returns {JSX.Element}
   */
  const renderStars = (count, activeCount) => (
    <div className="flex">
      {[...Array(count)].map((_, i) => (
        <Star
          key={i}
          size={20}
          className={`${
            i < activeCount ? "text-green-500 fill-green-500" : "text-gray-300"
          }`}
        />
      ))}
    </div>
  );

  /**
   * Renders average star ratings with fractional values.
   *
   * @param {number} avg - Average rating (e.g., 3.6)
   * @returns {JSX.Element}
   */
  const renderAverageStars = (avg) => {
    const full = Math.floor(avg);
    const hasHalf = avg - full >= 0.25 && avg - full < 0.75;
    const rounded = avg - full >= 0.75 ? full + 1 : full;

    return (
      <div className="flex">
        {[...Array(5)].map((_, i) => {
          if (i < rounded && !hasHalf) {
            return <Star key={i} size={20} className="text-green-500 fill-green-500" />;
          } else if (i === full && hasHalf) {
            return (
              <div key={i} className="relative">
                <Star size={20} className="text-gray-300" />
                <div className="absolute top-0 left-0 overflow-hidden w-1/2">
                  <Star size={20} className="text-green-500 fill-green-500" />
                </div>
              </div>
            );
          } else {
            return <Star key={i} size={20} className="text-gray-300" />;
          }
        })}
      </div>
    );
  };

  // === Render UI ===
  if (!stall)
    return <div className="p-4 text-center text-gray-500">Loading place details...</div>;

  return (
    <div className="pb-20 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b bg-white shadow-sm">
        <button onClick={() => navigate(-1)} className="text-green-700 font-medium">
          ← Back
        </button>
        <h1 className="text-green-700 text-lg font-semibold">HealthyAura</h1>
        <div className="w-10" />
      </div>

      {/* Eatery Info + Map + Reviews + Form */}
      {/* ... UI markup unchanged ... */}
    </div>
  );
}
