import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Users, Hourglass, Star, UserCircle, Clock } from "lucide-react";
import BottomNav from "../components/BottomNav";
import API from "../services/api";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

import markerIcon2x from "leaflet/dist/images/marker-icon-2x.png";
import markerIcon from "leaflet/dist/images/marker-icon.png";
import markerShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

export default function DetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [stall, setStall] = useState(null);
  const [crowd, setCrowd] = useState(null);
  const [reviews, setReviews] = useState([]);

  // Review form states
  const [healthScore, setHealthScore] = useState(0);
  const [hygieneScore, setHygieneScore] = useState(0);
  const [reviewText, setReviewText] = useState("");
  const [submitting, setSubmitting] = useState(false);

  // Fetch eatery info
  useEffect(() => {
    const fetchStall = async () => {
      try {
        const res = await API.get(`/api/eateries/${id}`);
        setStall(res.data);
      } catch (err) {
        console.error("❌ Failed to load eatery:", err);
      }
    };
    fetchStall();
  }, [id]);

  // Fetch crowd info
  useEffect(() => {
    const fetchCrowd = async () => {
      try {
        const res = await API.get(`/crowd/${id}`);
        setCrowd(res.data);
      } catch (err) {
        console.error("❌ Failed to load crowd info:", err);
      }
    };
    fetchCrowd();
  }, [id]);

  // Fetch reviews
  const fetchReviews = async () => {
    try {
      const res = await API.get(`/api/eateries/${id}/reviews`);
      if (Array.isArray(res.data)) setReviews(res.data);
      else setReviews([]);
    } catch (err) {
      console.error("❌ Failed to load reviews:", err);
    }
  };
  useEffect(() => {
    fetchReviews();
  }, [id]);

  // Map setup
  useEffect(() => {
    if (!stall || !stall.latitude || !stall.longitude) return;

    const existing = L.DomUtil.get("details-map");
    if (existing && existing._leaflet_id) existing._leaflet_id = null;

    const map = L.map("details-map", {
      center: [stall.latitude, stall.longitude],
      zoom: 17,
    });

    L.tileLayer(
      "https://www.onemap.gov.sg/maps/tiles/Original/{z}/{x}/{y}.png",
      { attribution: "Map data © OneMap Singapore" }
    ).addTo(map);

    L.marker([stall.latitude, stall.longitude])
      .addTo(map)
      .bindPopup(`<b>${stall.name}</b><br>${stall.address || ""}`)
      .openPopup();

    return () => map.remove();
  }, [stall]);

  // Submit review
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!healthScore || !hygieneScore || !reviewText) {
      alert("Please fill in all fields.");
      return;
    }

    try {
      setSubmitting(true);
      const payload = {
        healthScore,
        hygieneScore,
        textFeedback: reviewText,
      };
      const res = await API.post(`/api/eateries/${id}/reviews`, payload);
      console.log("✅ Review submitted:", res.data);
      setHealthScore(0);
      setHygieneScore(0);
      setReviewText("");
      fetchReviews();
    } catch (err) {
      console.error("❌ Failed to submit review:", err);
      alert("Failed to submit review.");
    } finally {
      setSubmitting(false);
    }
  };

  const renderStars = (count, activeCount) => (
    <div className="flex">
      {[...Array(count)].map((_, i) => (
        <Star
          key={i}
          size={20}
          className={`${
            i < activeCount ? "text-green-500 fill-green-500" : "text-gray-300"
          }`}
        />
      ))}
    </div>
  );

  if (!stall)
    return (
      <div className="p-4 text-center text-gray-500">
        Loading place details...
      </div>
    );

  return (
    <div className="pb-20 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b bg-white shadow-sm">
        <button onClick={() => navigate(-1)} className="text-green-700 font-medium">
          ← Back
        </button>
        <h1 className="text-green-700 text-lg font-semibold">HealthyAura</h1>
        <div className="w-10" />
      </div>

      {/* Eatery Info */}
      <div className="px-4 pt-4">
        <h2 className="text-xl font-semibold text-gray-900">
          {stall.name || "Eatery"}
        </h2>
        <p className="text-sm text-gray-500 mt-1">
          {stall.fullAddress || stall.address || "Address not available"}
        </p>
      </div>

      {/* Map */}
      <div className="px-4 mt-5">
        <div className="bg-white rounded-xl shadow-sm border p-3">
          <div id="details-map" className="w-full h-64 rounded-lg border" />
        </div>
      </div>

      <div className="px-4 mt-3">
        <button
          onClick={() =>
            window.open(
              `https://www.google.com/maps?q=${stall.latitude},${stall.longitude}`,
              "_blank"
            )
          }
          className="w-full bg-green-600 text-white py-3 rounded-lg hover:bg-green-700 transition text-sm font-medium"
        >
          Open in Maps
        </button>
      </div>

      {/* Crowd Info */}
      <div className="px-4 mt-6 space-y-4">
        <div className="bg-white rounded-xl p-4 shadow-sm">
          <div className="flex items-center space-x-2 text-green-700 mb-1">
            <Hourglass size={20} />
            <h3 className="text-lg font-semibold">Queue Status</h3>
          </div>
          {crowd ? (
            <p className="text-gray-700">
              Estimated Wait:{" "}
              <span className="font-semibold">{crowd.estimatedQueueMinutes} mins</span>
            </p>
          ) : (
            <p className="text-gray-400">Loading queue info...</p>
          )}
        </div>

        <div className="bg-white rounded-xl p-4 shadow-sm">
          <div className="flex items-center space-x-2 text-green-700 mb-1">
            <Users size={20} />
            <h3 className="text-lg font-semibold">Crowd Status</h3>
          </div>
          {crowd ? (
            <div
              className="rounded-lg py-2 px-3 text-center font-medium text-gray-800"
              style={{ backgroundColor: crowd.colorCode || "#E5E7EB" }}
            >
              {crowd.crowdLevel}
            </div>
          ) : (
            <p className="text-gray-400">Loading crowd info...</p>
          )}
        </div>
      </div>

      {/* Review Form */}
      <div className="px-4 mt-8">
        <h2 className="text-xl font-semibold text-gray-800 text-center mb-4">
          Leave a Review
        </h2>
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-xl p-4 shadow-sm space-y-4"
        >
          {/* Health Score */}
          <div>
            <label className="block font-medium text-gray-700 mb-1">
              Health Rating
            </label>
            <div className="flex">
              {[...Array(5)].map((_, i) => (
                <button
                  type="button"
                  key={i}
                  onClick={() => setHealthScore(i + 1)}
                  className="focus:outline-none"
                >
                  <Star
                    size={26}
                    className={`${
                      i < healthScore ? "text-green-500 fill-green-500" : "text-gray-300"
                    }`}
                  />
                </button>
              ))}
            </div>
          </div>

          {/* Hygiene Score */}
          <div>
            <label className="block font-medium text-gray-700 mb-1">
              Hygiene Rating
            </label>
            <div className="flex">
              {[...Array(5)].map((_, i) => (
                <button
                  type="button"
                  key={i}
                  onClick={() => setHygieneScore(i + 1)}
                  className="focus:outline-none"
                >
                  <Star
                    size={26}
                    className={`${
                      i < hygieneScore ? "text-green-500 fill-green-500" : "text-gray-300"
                    }`}
                  />
                </button>
              ))}
            </div>
          </div>

          {/* Feedback Text */}
          <div>
            <label className="block text-gray-700 font-medium mb-1">
              Your Feedback
            </label>
            <textarea
              className="w-full border border-gray-300 rounded-lg p-2 text-sm focus:ring-2 focus:ring-green-500"
              rows="3"
              placeholder="Write your review here..."
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
            ></textarea>
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="w-full bg-green-600 text-white py-2 rounded-lg hover:bg-green-700 transition font-medium"
          >
            {submitting ? "Submitting..." : "Submit Review"}
          </button>
        </form>
      </div>

      {/* Reviews Display */}
      <div className="px-4 mt-8 space-y-3">
        <h2 className="text-xl font-semibold text-gray-800 mb-2">Reviews</h2>

        {reviews.length > 0 ? (
          reviews.map((r) => (
            <div
              key={r.id}
              className={`bg-white rounded-xl p-4 shadow-sm flex items-start gap-3 ${
                r.isOwnReview ? "border-2 border-green-400" : ""
              }`}
            >
              <UserCircle size={40} className="text-green-600 flex-shrink-0" />
              <div className="flex-1">
                <div className="flex justify-between">
                  <p className="font-semibold text-gray-800">
                    {r.authorAlias} {r.isOwnReview && <span>(You)</span>}
                  </p>
                  <p className="text-xs text-gray-400 flex items-center gap-1">
                    <Clock size={14} />{" "}
                    {new Date(r.updatedAt || r.createdAt).toLocaleString()}
                  </p>
                </div>

                <p className="text-gray-600 text-sm mt-1">{r.textFeedback}</p>

                <div className="flex gap-3 mt-2 text-xs text-gray-600">
                  <div>
                    <span className="font-medium">Health:</span>{" "}
                    {renderStars(5, r.healthScore)}
                  </div>
                  <div>
                    <span className="font-medium">Hygiene:</span>{" "}
                    {renderStars(5, r.hygieneScore)}
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-500 text-sm text-center">
            No reviews yet. Be the first to share one!
          </p>
        )}
      </div>

      <BottomNav />
    </div>
  );
}
